mapper.update:
UPDATE product
SET sub_category = #{sub_category}, product_name = #{product_name}, price = #{price},add_day = sysdate, product_img = #{product_img}, brand = #{brand}, uuid = #{uuid}
WHERE product_no = #{product_no}

UPDATE img
SET filename = #{fileName}, uploadPath = #{uploadPath}, uuid = #{uuid}
WHERE product_no = #{product_no}
