mapper.select:
SELECT COUNT(*)
FROM member
WHERE id = #{id}
  AND pass = #{pass}

SELECT COUNT(*)
FROM member
WHERE id = #{id}

mapper.update:
UPDATE member
SET pass = #{pass}
WHERE id = #{id}

update member
set uploadImgFile=#{updateImgFile}
where id = #{id}

UPDATE member
SET mail_key = #{mail_key}
WHERE email = #{email}
  and id = #{id}

UPDATE member
SET mail_auth = 1
WHERE email = #{email}
  and mail_key = #{mail_key}

UPDATE member
SET pass = #{pass}
WHERE id = #{id}
  and email = #{email}

mapper.select:
SELECT id
FROM member
WHERE business_no = #{business_no}
  AND ceo = #{ceo}

mapper.update:
update member
set uuid=#{uuid}
where id = #{id}

mapper.select:
select id, business_no, business_name, ceo
from member
where id = #{id}
